import React, {useEffect, useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import GridList from "@material-ui/core/GridList";
import GridListTile from "@material-ui/core/GridListTile";
import GridListTileBar from "@material-ui/core/GridListTileBar";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import Items from "./Items";
import ToggleButton from '@material-ui/lab/ToggleButton';
import ToggleButtonGroup from '@material-ui/lab/ToggleButtonGroup';

const useStyles = makeStyles((theme) => ({
    // root: {
    //     flexGrow: 1,
    // },
    menuButton: {
    },
    title: {

    },
    header: {
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
    filtersWrapper: {
        marginLeft: 30,
        padding: 10,
    }
}));

export default function Filters(props) {
    const classes = useStyles();

    const [type, setType] = useState('any');
    const [price, setPrice] = useState('any');
    const [rank, setRank] = useState('asc');
    const [cart, setCart] = useState([]);

    let filterBooks = () => {
        return props.books.filter(filterByType).filter(filterByPrice).sort(sortByRank);
    }

    const filterByType = ( item ) => {
        if ( type === 'any' ) {
            return true;
        }

        if ( type === item.type ) {
            return true;
        }

        return false;
    }

    const filterByPrice = ( item ) => {
        if ( price === 'any' ) {
            return true;
        }

        if ( price == 1 ) {
            return item.price <= 10;
        }

        if ( price == 2 ) {
            return ( item.price > 10 && item.price <= 30 );
        }

        if ( price == 3 ) {
            return item.price > 30;
        }

        return true;
    }

    const sortByRank = ( a, b ) => {
        return rank === 'asc' ? a.rank - b.rank : b.rank - a.rank;
    }

    const updateType = (e, v) => setType(v);
    const updatePrice = (e, v) => setPrice(v);
    const updateRank = (e, v) => setRank(v);

    const addToCart = ( e ) => {

        let find = props.books.filter( ( item ) => item.rank == e.target.value );

        if( find.length != 0 ) {

        }
        let selectedItem = props.books.filter( ( item ) => item.rank == e.target.value )[0];
        setCart( cart => [...cart, selectedItem] );

        console.log(cart);
    }

    const sortedCart = () => {
        cart.map()
    }

    return (
        <div className={classes.root}>
            <div className={classes.filtersWrapper}>
                <div>
                    <b>Genre: </b>
                    <ToggleButtonGroup
                        value={type}
                        exclusive
                        onChange={updateType}
                    >
                        <ToggleButton value="any">
                            Any
                        </ToggleButton>
                        <ToggleButton value="Autobiography" aria-label="centered">
                            Autobiography
                        </ToggleButton>
                        <ToggleButton value="Nonfiction" aria-label="right aligned">
                            Nonfiction
                        </ToggleButton>

                    </ToggleButtonGroup>
                </div>
                <div>
                    <b>Price :</b>
                    <ToggleButtonGroup
                        value={price}
                        exclusive
                        onChange={updatePrice}
                    >
                        Genre :
                        <ToggleButton value="any">
                            Any
                        </ToggleButton>
                        <ToggleButton value="1" aria-label="centered">
                            <span>{'< 10'}</span>
                        </ToggleButton>
                        <ToggleButton value="2" aria-label="right aligned">
                            <span>{'11-30'}</span>
                        </ToggleButton>
                        <ToggleButton value="3" aria-label="right aligned">
                            <span>{'31+'}</span>
                        </ToggleButton>

                    </ToggleButtonGroup>
                </div>
                <div>
                    <b>Rank :</b>
                    <ToggleButtonGroup
                        value={rank}
                        exclusive
                        onChange={updateRank}
                    >
                        <ToggleButton value="asc">
                            Up
                        </ToggleButton>
                        <ToggleButton value="desc">
                            Down
                        </ToggleButton>
                    </ToggleButtonGroup>
                </div>

            </div>

            <Grid container spacing={3}>
                <Grid item xs={9}>
                    <Paper className={classes.paper}>
                        <Items books={filterBooks()} add={addToCart}/>
                    </Paper>
                </Grid>
                <Grid item xs={3}>
                    <Paper className={classes.paper}>Selected Items</Paper>
                    <ul>
                    {
                        cart.map( (item) => {
                            return <li key={item.rank}>{item.title}</li>
                        } )
                    }
                    </ul>
                </Grid>
            </Grid>
        </div>
    );
}
