import React, {useEffect, useState} from "react";
import './App.css';
import {makeStyles} from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Header from "./components/Header";
import Items from "./components/Items";

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
}));

function App() {

    const classes = useStyles();

    const fetchBreds = ['esho', 'beng', 'snow', 'tonk'];
    const [breeds, setBreeds] = useState([]);
    const [names, setNames] = useState([]);
    const [cats, setCats] = useState([]);
    let url = `https://api.thecatapi.com/v1/images/search?limit=12&has_breeds=1&breed_ids=${fetchBreds.join()}`;

    useEffect(() => {
        fetch(url)
            .then(res => res.json())
            .then(
                (result) => {

                    result.map((cat) => {
                        console.log(cat.breeds[0].name);
                        console.log(cat.breeds[0].id);
                    });

                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    console.log(error);
                }
            )
    });
    const img = 'http://ia800602.us.archive.org/view_archive.php?archive=/24/items/olcovers95/olcovers95-M.zip&file=956754-M.jpg';
    let shows = [
        {
            img: img,
            title: 'This is Title',
            author: 'Author'
        },
        {
            img: img,
            title: 'This is Title',
            author: 'Author'
        },
        {
            img: img,
            title: 'This is Title',
            author: 'Author'
        },
    ];
    return (
        <div className={classes.root}>
            <Header/>
            <ul>
                {names.map((name) => <li>{name}</li>)}
            </ul>

            {/*<Grid container spacing={3}>*/}
            {/*  <Grid item xs={9}>*/}
            {/*    <Paper className={classes.paper}>*/}
            {/*      <Items shows={shows}/>*/}
            {/*    </Paper>*/}
            {/*  </Grid>*/}
            {/*  <Grid item xs={3}>*/}
            {/*    <Paper className={classes.paper}>Selected Items</Paper>*/}
            {/*  </Grid>*/}
            {/*</Grid>*/}
        </div>
    );
}

export default App;
