import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import ListSubheader from '@material-ui/core/ListSubheader';
import IconButton from '@material-ui/core/IconButton';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-around',
        overflow: 'hidden',
        backgroundColor: theme.palette.background.paper,
    },
    gridList: {
        // margin: 5
    },
    image: {
        // height: 350,
        // width: 150,
    },
    icon: {
        color: 'rgba(255, 255, 255, 0.54)',
    },
}));

/**
 * The example data is structured as follows:
 *
 * import image from 'path/to/image.jpg';
 * [etc...]
 *
 * const tileData = [
 *   {
 *     img: image,
 *     title: 'Image',
 *     author: 'author',
 *   },
 *   {
 *     [etc...]
 *   },
 * ];
 */
export default function Items( props ) {
    const classes = useStyles();
    let tileData = props.books.map( (book) => {

        return {
            img: book.book_image,
            title: book.title,
            author: book.author
        }
    } );
    return (
        <div className={classes.root}>
            <GridList cols={3} cellHeight={400} cellWidth={200}>
                <GridListTile key="Subheader" cols={3} style={{ height: 'auto' }}>
                    <ListSubheader component="div">Books</ListSubheader>
                </GridListTile>
                {tileData.map((tile) => (
                    <GridListTile key={tile.img} className={classes.gridList}>
                        <img src={tile.img} alt={tile.title} className={classes.image}/>
                        <GridListTileBar
                            title={tile.title}
                            subtitle={<span>by: {tile.author}</span>}
                            // actionIcon={
                            //     <IconButton aria-label={`info about ${tile.title}`} className={classes.icon}>
                            //     </IconButton>
                            // }
                        />
                    </GridListTile>
                ))}
            </GridList>
        </div>
    );
}
