import React, {useEffect, useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import GridList from "@material-ui/core/GridList";
import GridListTile from "@material-ui/core/GridListTile";
import GridListTileBar from "@material-ui/core/GridListTileBar";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import Items from "./Items";

const useStyles = makeStyles((theme) => ({
    root: {
    },
    menuButton: {
    },
    title: {

    },
    header: {
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
}));

export default function Filters(props) {
    const classes = useStyles();

    const [state, setState] = useState( {
        type: 'any',
        price: 'any',
        rank: 'asc',
        books: props.books,
    } );
    // useEffect( ()=> {
    //     props.filterBooks( props.books.filter( (book) => book.price < 20 ) );
    // } );

    const filterBooks = ( e ) => {
        setState( (prevState) => {

            let current = prevState.books.filter(filterByPrice).sort(sortByPrice);

            return {
                ...prevState,
                books: current
            }
        } );
    }

    const filterByType = ( item ) => {
        if ( state.type === 'any' ) {
            return true;
        }

        if ( state.type === item.type ){
            return true;
        }

        return false;
    }

    const filterByPrice = ( item ) => item.price < 20;
    const sortByRank = ( a, b ) => a.rank > b.rank;


    return (
        <div className={classes.root}>
           <button value="all" onClick={filterBooks}>Type All</button>
           <button value="ball" onClick={filterBooks}>Type Ball</button>
            <Grid container spacing={3}>
                <Grid item xs={9}>
                    <Paper className={classes.paper}>
                        <Items books={state.books}/>
                    </Paper>
                </Grid>
                <Grid item xs={3}>
                    <Paper className={classes.paper}>Selected Items</Paper>
                </Grid>
            </Grid>
        </div>
    );
}
