import React, {useEffect, useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import GridList from "@material-ui/core/GridList";
import GridListTile from "@material-ui/core/GridListTile";
import GridListTileBar from "@material-ui/core/GridListTileBar";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import Items from "./Items";

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    menuButton: {
    },
    title: {

    },
    header: {
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
}));

export default function Filters(props) {
    const classes = useStyles();

    const [type, setType] = useState('any');
    const [price, setPrice] = useState('any');
    const [rank, setRank] = useState('asc');
    const [books, setBooks] = useState(props.books);


    const filterBooks = () => {
        return props.books.filter(filterByType);
    }

    // useEffect( filterBooks );

    const filterByType = ( item ) => {
        if ( type === 'any' ) {
            return true;
        }

        if ( type === item.type ) {
            return true;
        }

        return false;
    }

    const filterByPrice = ( item ) => {
        if ( price === 'any' ) {
            return true;
        }

        if ( price == 1 ) {
            return item.price <= 10;
        }

        if ( price == 2 ) {
            return ( item.price > 10 && item.price <= 30 );
        }

        if ( price == 3 ) {
            return item.price > 30;
        }

        return true;
    }

    const sortByRank = ( a, b ) => a.rank > b.rank;

    const updateType = (e) => setType(e.target.value);
    const updatePrice = (e) => setPrice(e.target.value);
    const updateRank = (e) => setRank(e.target.value);

    return (
        <div className={classes.root}>
            <ToggleButtonGroup
                value={type}
                exclusive
                onChange={updateType}
            >
                <ToggleButton value="any">
                    Any
                </ToggleButton>
                <ToggleButton value="Autobiography" aria-label="centered">
                    Autobiography
                </ToggleButton>
                <ToggleButton value="Nonfiction" aria-label="right aligned">
                    Nonfiction
                </ToggleButton>

            </ToggleButtonGroup>
           <button value="any" onClick={updateType}>Any</button>
           <button value="Autobiography" onClick={updateType}>Auto</button>
           <button value="Nonfiction" onClick={updateType}>NonFiction</button>
            <Grid container spacing={3}>
                <Grid item xs={9}>
                    <Paper className={classes.paper}>
                        <Items books={filterBooks}/>
                    </Paper>
                </Grid>
                <Grid item xs={3}>
                    <Paper className={classes.paper}>Selected Items</Paper>
                </Grid>
            </Grid>
        </div>
    );
}
